import React from "react";
import {CommodityListApi} from "../../api/CommodityListApi";
import {Image, message, Popconfirm, Space, Table, Tag} from 'antd';
import {DeleteOutlined, EditOutlined} from "@ant-design/icons";
import {log} from "util";

const {Column} = Table;

export let typeMap:any = {
    '1': "果茶家族",
    '2':"茗茶/牛乳",
    '3': "波波家族",
    '4': "醇香咖啡",
}

class CommodityTable extends React.Component<any> {
    getDataSource(){
        this.setState({
            loading: true
        })
        CommodityListApi.getCommodityList().then(res => {
            console.log(res);
            this.setState({
                commodityList: res.data,
                loading: false
            })
        });
    }

    UNSAFE_componentWillMount() {
        this.setState({
            commodityList: [],
            loading: true
        })
        this.getDataSource();
    }
    editCommodity(data:any){
        console.log(data)
        this.props.parent.updateCommodity(data);
    }
    deleteCommodity(data:any){
        CommodityListApi.deleteCommodity(data.id).then(res=>{
            if(res.code === 0){
                message.success("删除成功");
                this.props.parent.refresh();
            }else{
                message.error("删除失败")
            }
        })
    }

    render() {
        return (
            <>
                <Table dataSource={
                    // @ts-ignore
                    this.state.commodityList} rowKey={(record: any) => record.id} loading={this.state.loading}>
                    <Column title="商品图片" dataIndex="image" key="image" render={(url:string)=>{
                        return (
                            <Image width={50} src={url}
                                   fallback={"data:image/png;base64,"}
                            />
                        )
                    }}/>
                    <Column title="商品名" dataIndex="name" key="name"/>
                    <Column title="商品价格" dataIndex="price" key="id"/>
                    <Column
                        title="类别"
                        dataIndex="type"
                        key="id"
                        render={type => (
                            <Tag color="blue">
                                {typeMap[type]}
                            </Tag>
                        )}
                    />
                    <Column
                        title="操作"
                        key="operate"
                        render={(text, record: any) => {
                            return (
                                <Space size="middle">
                                    <a style={{color: "lightseagreen"}} onClick={()=>this.editCommodity(record)}>
                                        <EditOutlined  />
                                        &nbsp;修改
                                    </a>
                                    <Popconfirm
                                        title="确认删除该商品吗?"
                                        onConfirm={()=>this.deleteCommodity(record)}
                                        okText="是"
                                        cancelText="否"
                                    >
                                        <a style={{color: "red"}}>
                                            <DeleteOutlined />
                                            &nbsp;删除
                                        </a>
                                    </Popconfirm>
                                </Space>
                            )
                        }}
                    />
                </Table>
            </>
        );
    }
}


export default CommodityTable;
